# Split and clean the input into individual numbers
numbers = list(map(float, input_str.replace(',', '').split()))

# Multiply each value by 0.075
scaled_numbers = [round(n * 0.0075, 6) for n in numbers]

# Group into sublists of 3 for comma-separated sets
grouped = [scaled_numbers[i:i+3] for i in range(0, len(scaled_numbers), 3)]

# Format each group into a string
formatted_output = ', '.join([' '.join(map(str, group)) for group in grouped])

print(formatted_output)